<?phpdefined('BASEPATH') OR exit('No direct script access allowed');class Myprofile extends CI_Controller {	function __construct() {        parent::__construct();		error_reporting(0);		$this->load->model('admin_model');    }		public function index()	{		if($this->session->userdata('adm_logged_in') == true)		{			$adminid            = $this->session->userdata('adm_admin_id');			$data['module']     = 'myprofile';			$data['adminid']    = $adminid;			$data['result']     = $this->admin_model->viewrecord($adminid);			$data['mtitle']     = 'My Profile : Dashboard : My PhD Wallet : '.WEBNAME;			$data['main_content'] = 'admin/myprofile';			$this->load->view('common/template.php',$data);		}		else		{			redirect(base_url());		}	}}